import React, { useState } from 'react';
import './registrationForm.css';

const RegistrationForm = ({ onClose }) => {
    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const handleFirstNameChange = (e) => {
        setFirstName(e.target.value);
    };

    const handleLastNameChange = (e) => {
        setLastName(e.target.value);
    };

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Registration form submitted:', { firstName, lastName, email, password, confirmPassword });
        onClose(); // Close the form or navigate to another page
    };

    return (
        <div className="registration-form">
            <h2>Register</h2>
            <form className="registration-form__form" onSubmit={handleSubmit}>
                <label className="registration-form__label">First Name:</label>
                <input className="registration-form__input" type="text" value={firstName} onChange={handleFirstNameChange} />

                <label className="registration-form__label">Last Name:</label>
                <input className="registration-form__input" type="text" value={lastName} onChange={handleLastNameChange} />

                <label className="registration-form__label">Email:</label>
                <input className="registration-form__input" type="email" value={email} onChange={handleEmailChange} />

                <label className="registration-form__label">Password:</label>
                <input className="registration-form__input" type="password" value={password} onChange={handlePasswordChange} />

                <label className="registration-form__label">Confirm Password:</label>
                <input className="registration-form__input" type="password" value={confirmPassword} onChange={handleConfirmPasswordChange} />

                <button className="registration-form__button" type="submit">Register</button>
            </form>
        </div>
    );
};

export default RegistrationForm;
