import React, { useState } from 'react';
import './PostAd.css';

const PostAd = ({ onClose }) => {
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [paymentInfo, setPaymentInfo] = useState({
        cardNumber: '',
        expiryDate: '',
        cvv: '',
    });

    const handleSubmit = (event) => {
        event.preventDefault();
        if (validatePaymentInfo(paymentInfo)) {
            console.log('Payment successful!');
            // Process ad submission
            console.log('Form submitted:', { title, description });
            onClose();
        } else {
            console.log('Payment information is invalid.');
            // Display error message or prompt for correct payment info
        }
    };

    const validatePaymentInfo = (paymentInfo) => {
        // Example validation - implement your own validation logic here
        return paymentInfo.cardNumber && paymentInfo.expiryDate && paymentInfo.cvv;
    };

    const handlePaymentInputChange = (event) => {
        const { name, value } = event.target;
        setPaymentInfo({
            ...paymentInfo,
            [name]: value,
        });
    };

    return (
        <div className="post-ad-container">
            <h2>Post Ad</h2>
            <form onSubmit={handleSubmit}>
                {/* Ad details */}
                <input
                    type="text"
                    placeholder="Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    required
                ></textarea>
                {/* Payment information */}
                <input
                    type="text"
                    placeholder="Card Number"
                    name="cardNumber"
                    value={paymentInfo.cardNumber}
                    onChange={handlePaymentInputChange}
                    required
                />
                <input
                    type="text"
                    placeholder="Expiry Date"
                    name="expiryDate"
                    value={paymentInfo.expiryDate}
                    onChange={handlePaymentInputChange}
                    required
                />
                <input
                    type="text"
                    placeholder="CVV"
                    name="cvv"
                    value={paymentInfo.cvv}
                    onChange={handlePaymentInputChange}
                    required
                />
                {/* Submit button */}
                <button type="submit">Submit</button>
            </form>
        </div>
    );
};

export default PostAd;
