// Header.js
import React, { useState } from 'react';
import RegistrationForm from './RegistrationForm'; // Import the RegistrationForm component

const Header = () => {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <header className="header">
            <h1>Welcome to BargainHunter</h1>
            <nav className="nav">
                <ul>
                    <li><a href="#">Sell Your Items</a></li>
                    <li><a href="#">Buy Items</a></li>
                    <li><a href="#">List Home</a></li>
                    <li><a href="#" onClick={openModal}>Register</a></li>
                    <li><a href="#">Login</a></li>
                </ul>
            </nav>
            <RegistrationForm isOpen={isModalOpen} onClose={closeModal} />
        </header>
    );
};

export default Header;
