/* SellPage.js */

import React, { useState } from 'react';
import './sellPage.css';

const SellPage = () => {
    const [itemName, setItemName] = useState('');
    const [itemDescription, setItemDescription] = useState('');
    const [itemPrice, setItemPrice] = useState('');
    const [currencyType, setCurrencyType] = useState('USD'); // Default currency type

    const handleItemNameChange = (e) => {
        setItemName(e.target.value);
    };

    const handleItemDescriptionChange = (e) => {
        setItemDescription(e.target.value);
    };

    const handleItemPriceChange = (e) => {
        setItemPrice(e.target.value);
    };

    const handleCurrencyTypeChange = (e) => {
        setCurrencyType(e.target.value);
    };

    const handleImageUpload = (e) => {
        const file = e.target.files[0];
        setItemImage(file);
    };

    const handleListItem = () => {
        // Logic to handle listing the item, e.g., sending data to the server
        console.log('Item listed:', { itemName, itemDescription, itemPrice, currencyType });
    };

    return (
        <div className="sell-page">
            <h2>List an Item</h2>
            <form className="sell-form">
                <label htmlFor="itemName">Title:</label>
                <input type="text" id="itemName" value={itemName} onChange={handleItemNameChange} />

                <label htmlFor="itemDescription">Description:</label>
                <textarea id="itemDescription" value={itemDescription} onChange={handleItemDescriptionChange}></textarea>

                <label htmlFor="itemPrice">Price:</label>
                <input type="text" id="itemPrice" value={itemPrice} onChange={handleItemPriceChange} />
                <select value={currencyType} onChange={handleCurrencyTypeChange}>
                    <option value="USD">USD</option>
                    <option value="EUR">EUR</option>
                    <option value="GBP">GBP</option>
                    <option value="INR">INR</option>
                </select>

                <label htmlFor="itemImage">Image:</label>
                <input type="file" id="itemImage" accept="image/*" onChange={handleImageUpload} />

                <button type="button" onClick={handleListItem}>List Item</button>
            </form>
        </div>
    );
};

export default SellPage;
